import { Routes, Route, NavLink, useNavigate } from "react-router-dom";
import React, { ReactNode } from "react";
import { UserType } from "../features/authentication/types";

type AuthProviderPropsType = {
  children: ReactNode;
};

type SessionType = {
  user: UserType | null;
};

const AuthContext = React.createContext<SessionType>({
  user: null,
});

const AuthProvider = ({ children }: AuthProviderPropsType) => {
  const navigate = useNavigate();

  const [user, setUser] = React.useState<UserType | null>(null);

  const logInAs = async (user: UserType) => {
    setUser(user);
    navigate("/");
  };

  const handleLogout = () => {
    setUser(null);
  };

  const isAuthenticated = () => {
    return user != null;
  };

  const value = {
    user,
    logInAs,
    handleLogout,
    isAuthenticated,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

const useAuthenticate = () => {
  const context = React.useContext(AuthContext);
  if (context === undefined) {
    throw new Error("must be used within a AuthProvider");
  }
  return context;
};

export { AuthProvider, useAuthenticate };
