import { useCallback, useEffect, useMemo, useState } from "react";

declare var process: {
  env: {
    REACT_APP_REMITANO_BACKEND_URL: string;
  };
};

type useFetchProps = {
  path: string;
  method: "POST" | "GET" | "PUT" | "PATCH" | "DELETE";
  onSuccess?: (data: Promise<any>) => any;
};

const useFetch = ({ path, method, onSuccess }: useFetchProps) => {
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [total, setTotal] = useState(0);

  const fetchApi = useCallback(
    (params: any) => {
      const url =
        `${process.env.REACT_APP_REMITANO_BACKEND_URL}/${path}?` +
        (method === "GET" ? new URLSearchParams(params) : "");

      const fetch_params =
        method === "GET"
          ? { method: method, headers: { "Content-Type": "application/json" } }
          : {
              method: method,
              body: JSON.stringify(params),
              headers: { "Content-Type": "application/json" },
            };

      fetch(url, fetch_params).then((res) => {
        setTotal(parseInt(res.headers.get("Total") || "0"));
        if (res.ok) {
          res.json().then((res_body) => {
            onSuccess?.(res_body);
            setError(res_body.error);
            setData(res_body.data);
            setIsLoading(false);
          });
        } else {
          res.json().then((res_body) => {
            setError(res_body.error);
            setData(res_body.data);
            setIsLoading(false);
          });
        }
      });
    },
    [path]
  );

  return { data, isLoading, error, total, fetchApi };
};
export default useFetch;
