import { Box, Grid, Typography } from "@mui/material";
import React from "react";
import VideoIframe from "./videoIframe";
import { VideoType } from "../types";
import ThumbUpIcon from '@mui/icons-material/ThumbUpAlt';
import ThumbUpOffIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbDownIcon from '@mui/icons-material/ThumbDownAlt';
import ThumbDownOffIcon from '@mui/icons-material/ThumbDownOffAlt';

type VideoItemProps = {
  video: VideoType;
};

const VideoItem = ({ video }: VideoItemProps) => {
  return (
    <Box mb={3}>
      <Grid container spacing={5}>
        <Grid item xs={5} sx={{ minHeight: '20vh' }}>
          <VideoIframe video_id={video.youtubeId} />
        </Grid>
        <Grid item xs={7}>
          <Typography variant="h5" color="red" fontWeight={600}>
            {video.title}
          </Typography>
          <Typography>
            Shared by: {video.sharedBy}
          </Typography>
          <Box sx={{ display: 'flex' }}>
            <Typography>
              {video.upvoteCount}
            </Typography>
            <ThumbUpOffIcon/>
            <Typography ml={4}>
              {video.downvoteCount}
            </Typography>
            <ThumbDownOffIcon/>
            {/* <ThumbUpIcon/>
            <ThumbDownIcon/> */}
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
};

export default VideoItem;
