import React, { useEffect, useState } from 'react'
import useFetch from '../../../hooks/useFetch'

type useRegisterProps = {
  email: string,
  password: string,
  passwordConfirmation: string,
  onSuccess: () => void
}

const useRegister = ({ email, password, passwordConfirmation, onSuccess }: useRegisterProps) => {
  const { data, error, fetchApi } = useFetch({
    path: '/api/v1/users', method: 'POST', onSuccess: () => onSuccess()
  })

  useEffect(()=>{
    fetchApi({
      email: email,
      password: password,
      password_confirmation: passwordConfirmation
    });
  }, [])

  return { data, error, submit: () => setIsIdle(false) }
}

export default useRegister
