import { useCallback, useEffect, useMemo, useState } from "react";

declare var process: {
  env: {
    REACT_APP_REMITANO_BACKEND_URL: string;
  };
};

type useFetchProps = {
  path: string;
  method: "POST" | "GET" | "PUT" | "PATCH" | "DELETE";
  params: any;
  onSuccess?: () => any;
};

const useFetch = ({ path, method, onSuccess }: useFetchProps) => {
  const [params, setParams] = useState({})
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [total, setTotal] = useState(0);

  const url =
    `${process.env.REACT_APP_REMITANO_BACKEND_URL}/${path}?` +
    (method === "GET" ? new URLSearchParams(params) : "");

  const fetch_params =
    method === "GET"
      ? { method: method }
      : {
          method: method,
          body: JSON.stringify(params),
        };

  const useFetchApi = useCallback((params: any) => {

    fetch(url, fetch_params)
      .then((res) => {
        setTotal(parseInt(res.headers.get("Total") || "0"));
        if (res.ok) onSuccess?.()

        return res.json();
      })
      .then((response) => {
        setError(response.error);
        setData(response.data);
        setIsLoading(false);
      });
  }, [url]);

  return { data, isLoading, error, total, useFetchApi };
};
export default useFetch;
