import React, { useState } from 'react'
import useLoadVideos from '../features/videos/hooks/useLoadVideos'
import { Box, Container, Pagination } from '@mui/material'
import VideoItem from '../features/videos/components/videoItem'

type Props = {}

const HomePage = (props: Props) => {
  const [page, setPage] = useState(0)
  const { data: videos } = useLoadVideos({ pageIndex: page })

  return (
    <Container>
      {videos.map(video =>
        <VideoItem key={video.id} video={video} />
      )}
      <Box sx={{ display: 'flex', justifyContent: 'center' }}>
        <Pagination count={4} page={page} onChange={(e, page) => setPage(page)} />
      </Box>
    </Container>
  )
}

export default HomePage
