import {
  Routes,
  Route,
  NavLink,
  useNavigate,
} from 'react-router-dom';
import React, { ReactNode } from 'react';
import { UserType } from '../features/authentication/types';

type AuthProviderPropsType = {
  children: ReactNode
};

type SessionType = {
  user: UserType | null
}

const AuthContext = React.createContext<SessionType>({
  user: null,
});

const fakeAuth: (()=> Promise<string>) = () =>
  new Promise<string>((resolve) => {
    setTimeout(() => resolve('2342f2f1d131rf12'), 250);
  });

const AuthProvider = ({ children } : AuthProviderPropsType ) => {
  const navigate = useNavigate();

  const [user, setUser] = React.useState<UserType | null>(null);

  const logInAs = async (user: UserType) => {
    setUser(null);
    navigate('/');
  };

  const handleLogout = () => {
    setUser(null);
  };

  const isAuthenticated = () => {
    user != null
  }

  const value = {
    user: user,
    logInAs: logInAs,
    handleLogout: handleLogout,
    isAuthenticated: isAuthenticated
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
